/**
 * 取得 icon HTML
 * @param {string} name - icon 名稱（不含 mdi- 前綴）
 * @param {string} className - 額外的 CSS class
 * @returns {string} SVG HTML
 */
function getIcon(name, className = '') {
  const iconName = name.replace('mdi-', '');
  const svg = Icons[iconName] || Icons['information'];
  if (className) {
    return svg.replace('<svg', `<svg class="${className}"`);
  }
  return svg;
}
